import React from 'react-native';
var {
  Navigator,
  Text,
  View,
  TabBarIOS,
  StyleSheet,
  PixelRatio
} = React;

var Routes = require('./Navigation/Routes');
var RouteMapper = require('./Navigation/RouteMapper');
var NavigationBarRouteMapper = require('./Navigation/NavigationBarRouteMapper');
var NavigationView = require('./Navigation/NavigationView');
var styles = require('./Navigation/NavigationBar.styles');

var base64Icon = 'data:image/png;base64,';

var ROUTE_STACK = [
  Routes.home,
  Routes.next
];
var INITIAL_ROUTE_INDEX = 0;

var App = React.createClass({
  renderScene: function(route, navigator) {
    return (
      <RouteMapper route={route} navigator={navigator}/>
    );
  },

  render: function() {
    return this.renderMainNavigator();
  },

  renderMainNavigator: function() {
    return (
      <Navigator
        style={styles.appContainer}
        ref={(navigator) => {
          this._navigator = navigator;
        }}
        initialRoute={ROUTE_STACK[INITIAL_ROUTE_INDEX]}
        initialRouteStack={ROUTE_STACK}
        renderScene={this.renderScene}
        configureScene={() => ({
            ...Navigator.SceneConfigs.HorizontalSwipeJump
          })
        }
        barTintColor="#424244"
        navigationBar={
          <TabNavigationBar
            onTabIndex={(index) => {
              this._navigator.jumpTo(ROUTE_STACK[index]);
            }}
            style={styles.navBarContainer}
          />
        }
      />
    );
  },

});

var TabNavigationBar = React.createClass({
  getInitialState() {
      return {
        tabIndex: 0
      }
  },

  handleWillFocus(route) {
    var tabIndex = ROUTE_STACK.indexOf(route);
    this.setState({ tabIndex, });
  },

  render: function() {
    return (
      <TabBarIOS>
        <TabBarIOS.Item
          icon={{uri: base64Icon, scale: 3}}
          title="Home"
          selected={this.state.tabIndex === 0}
          onPress={() => {
            this.props.onTabIndex(0);
            //this.setState({ tabIndex: 0, });
          }}>
          <View />
        </TabBarIOS.Item>
        <TabBarIOS.Item
          icon={{uri: base64Icon, scale: 3}}
          title="NExt"
          selected={this.state.tabIndex === 1}
          onPress={() => {
            this.props.onTabIndex(1);
            //this.setState({ tabIndex: 1, });
          }}>
          <View />
        </TabBarIOS.Item>
      </TabBarIOS>

    );
  }

});

var styles = StyleSheet.create({
  button: {
    backgroundColor: 'white',
    padding: 15,
    borderBottomWidth: 1 / PixelRatio.get(),
    borderBottomColor: '#CDCDCD',
  },
  buttonText: {
    fontSize: 17,
    fontWeight: '500',
  },
  appContainer: {
    overflow: 'hidden',
    backgroundColor: '#dddddd',
    flex: 1,
  },
  messageText: {
    fontSize: 17,
    fontWeight: '500',
    padding: 15,
    marginTop: 50,
    marginLeft: 15,
  },
  scene: {
    flex: 1,
    paddingTop: 20,
    backgroundColor: '#EAEAEA',
  },
  tabs: {
    height: 50,
  }
});

module.exports = App;
